  metadata.source = subDir ? "".concat(subDir, "/").concat(path.basename(file)) : path.basename(file);
  metadata.versioned_source = metadata.source;
  if (!metadata.title) {
    metadata.title = metadata.id;
  }
  var langPart = env.translation.enabled || siteConfig.useEnglishUrl ? "".concat(language, "/") : '';
  var versionPart = '';
  if (env.versioning.enabled) {
    metadata.version = 'next';
