  // separate this out for Nuclide treating @generated as readonly
  ' */\n' + 'module.exports = ').concat(JSON.stringify(metadatas, null, 2), ";\n"));
}

// process metadata for blog posts and save into core/MetadataBlog.js
function generateMetadataBlog() {
  var config = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : siteConfig;
  var metadatas = [];
  var files = glob.sync("".concat(CWD, "/blog/**/*.*"));
  files.sort().reverse().forEach(function (file) {
    var extension = path.extname(file);
    if (extension !== '.md' && extension !== '.markdown') {
      return;
